mapper.insert:
-- 查询待插入角色的分组
SELECT `group` INTO @targetGroup
FROM `role`
WHERE `id` = #{roleId} AND `is_deleted` = 0;

-- 逻辑删除同用户+同分组的旧角色
IF @targetGroup IS NOT NULL THEN
UPDATE `user_role` ur
    INNER JOIN `role` r ON ur.`role_id` = r.`id`
    SET ur.`is_deleted` = 1,
WHERE ur.`user_id` = #{userId}
  AND ur.`is_deleted` = 0
  AND r.`group` = @targetGroup;

-- 插入新记录
INSERT INTO `user_role` (
    `id`,
    `role_id`,
    `user_id`,
) VALUES (
             #{id},
             #{roleId},
             #{userId},
         );
END IF;
